import random

def encode(num: int, char_len: int) -> str:
    r = random.randint(0, len(chars) - 1)
    char_map = chars[r]    
    result = [None] * char_len
    result[-1] = str(r)
    i = char_len - 2
    while i >= 0:
        result[i] = char_map[num % len(char_map)]
        num = num // len(char_map)
        i -= 1    
    return ''.join(result)

